
public class LogicalOperations1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//1. logical negation !
		//2. logical conjunction &&
		//3. logical disjunction ||
		boolean p = true;
		boolean negation = !p;
		System.out.println("Logical negtion of p being " + p+ " is " + negation);
		p = false;
		negation = !p;
		System.out.println("Logical negation of p being " + p+" is " +negation);
		
		
	}

}
